import React, { useContext } from 'react';
import { RoomContext } from '../context';
import Title from './Title';


const getUnique = (items,value) => {
    return [...new Set(items.map(item =>item[value]))]
}
export default function RoomFilter({rooms}) {
const context = useContext(RoomContext)
 const {handelChange,type,capacity,price,minPrice,maxPrice,minSize,maxSize,breakfast,pets
 } = context;

 let types = getUnique(rooms,"type")
 types = ["all",...types]
//  console.table(types)

 types = types.map((item,index)=> {
     return <option value={item} key={index}>{item}</option>
 })
 let people =getUnique(rooms,'capacity');
 people = people.map((item,index)=> {
 return <option key={index} value={item}>{item}</option>
 })
    return (
        <section className="filter-container">
                <Title title="search rooms"/>
                <form className="filter-form">
                    {/* select type */}
                    <div className="form-group">
                    <label htmlFor="type">room type</label>
                    <select name="type" id="type" value={type} className="form-control"
                    onChange={handelChange}>
                            {types}
                    </select>
                    {/* {end select type} */}
                    {/* select guest*/}
                    </div>
                     <div className="form-group">
                    <label htmlFor="capacity">Guests</label>
                    <select name="capacity" id="capacity" value={capacity} className="form-control"
                    onChange={handelChange}>
                            {people}
                    </select>
                    {/* {end select guest} */}
                    {/*room price */}
                    <div className="form-group">
                    <label htmlFor="price">room price ${price}</label>
                    <input type="range" name="price" min={minPrice} max={maxPrice}id="price" value={price} onChange={handelChange} />
                    </div>
                    {/*end price */}
                    </div>
                </form>
        </section>
    )
}
